<script setup>
	import { reactive } from 'vue';
	import sheep from '@/sheep';
	import { onLoad } from '@dcloudio/uni-app';
  import BargainApi from '@/sheep/api/promotion/bargain'

	const state = reactive({
		id: '',
		buyPrice: '',
		bargainInfo: {},
		bgColor: {
			'bgColor': '#333333',
			'Color': '#fff',
			'width': '44rpx',
			'timeTxtwidth': '16rpx',
			'isDay': true
		},
		bargainUserHelpList: [],
		couponsHidden: true,
	});


	async function getBargainDetails() {
		let {
			data,
			code
		} = await BargainApi.getBargainActivityDetail({
			id: state.id
		})
		if (code == 0) {
			state.bargainInfo = data;
			state.buyPrice = state.bargainInfo.bargainFirstPrice;
		}

	}
	onLoad(function(options) {
		if (options.hasOwnProperty('id')) {
			options.id ? state.id = options.id : state.id = ''
		}
		getBargainDetails()
	})
	// 工具函数
	function fen2yuan(price) {
		return (price / 100.0).toFixed(2)
	}
</script>

